import PropTypes from 'prop-types';
import React from 'react';
import style from './feedback.module.css';

export default class FeedbackApp extends React.Component{
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
    total: 0,
    percentage: 0
  }
  countGoodFeedback = () => {
    this.setState(prevState => ({
      good: prevState.good + 1,
    }));
  }
  countNeutralFeedback = () => {
    this.setState(prevState => ({
      neutral: prevState.neutral + 1,
    }));
  }
  countBadFeedback = () => {
    this.setState(prevState => ({
      bad: prevState.bad + 1,
    }));
  }
  countTotalFeedback = () => {
    this.setState(prevState => ({
      total: prevState.neutral + prevState.bad + prevState.good,
    }));
    console.log(this.state.total);
  }
  countPositiveFeedbackPercentage = () => {

  }

  render () {
    return (
      <div>
        <div className={style.buttonList}>
          <button type='button' className={style.button} onClick={this.countGoodFeedback}>Good</button>
          <button type='button' className={style.button} onClick={this.countNeutralFeedback}>Neutral</button>
          <button type='button' className={style.button} onClick={this.countBadFeedback}>Bad</button>
        </div>

        <ul className={style.list}>Statistics
          <li className={style.item}>
            <span className={style.label}>Good</span>
            <span className={style.number}></span>
          </li>

          <li className={style.item}>
            <span className={style.label}>Neutral</span>
            <span className={style.number}></span>
          </li>

          <li className={style.item}>
            <span className={style.label}>Bad</span>
            <span className={style.number}></span>
          </li>

          <li className={style.item}>
            <span className={style.label}>Total</span>
            <span className={style.number}></span>
          </li>

          <li className={style.item}>
            <span className={style.label}>Positive feedback</span>
            <span className={style.number}></span>
          </li>

        </ul>
      </div>
    )
  }
}
