import React from "react";
import style from './feedback.module.css';

const Controls = ({onGoodFeedback, onNeutralFeedback, onBadFeedback}) => (
    <div className={style.controls}>
        <p className={style.title}>Please leave feedback</p>
        <div className={style.buttonList}>
            <button type='button' className={style.button} onClick={onGoodFeedback}>Good</button>
            <button type='button' className={style.button} onClick={onNeutralFeedback}>Neutral</button>
            <button type='button' className={style.button} onClick={onBadFeedback}>Bad</button>
        </div>
    </div>
)
export default Controls;
