import PropTypes from 'prop-types';
import React from 'react';
import style from './feedback.module.css';
import Statistics from './statistics'
import Controls from './controls'

export default class FeedbackApp extends React.Component{
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
    total: 0,
    percentage: 0
  }
  countGoodFeedback = () => {
    this.setState(prevState => ({
      good: Number(prevState.good + 1),
      total: this.state.good + this.state.neutral + this.state.bad + 1,
    }));
  }
  countNeutralFeedback = () => {
    this.setState(prevState => ({
      neutral: Number(prevState.neutral + 1),
      total: this.state.good + this.state.neutral + this.state.bad + 1,
    }));
  }
  countBadFeedback = () => {
    this.setState(prevState => ({
      bad: Number(prevState.bad + 1),
      total: this.state.good + this.state.neutral + this.state.bad + 1,
    }));
  }
  countTotalFeedback = () => {
    this.setState({
        total: this.state.good + this.state.neutral + this.state.bad,
      })

  }
  countPositiveFeedbackPercentage = () => {

  }

  render () {
    return (
      <div className={style.feedback}>
        {!this.state.total && <p className={style.title}>There is no feedback</p>}
        <Controls 
          onGoodFeedback={this.countGoodFeedback}
          onNeutralFeedback={this.countNeutralFeedback}
          onBadFeedback={this.countBadFeedback}
          />
        <Statistics
          good = {this.state.good}
          neutral ={this.state.neutral}
          bad ={this.state.bad}
          total ={this.state.total}
          percentage ={this.state.percentage}
        />
      </div>
    )
  }
}
